var WebSocketServer=require("ws").Server,wss=new WebSocketServer({port:8080});wss.on("connection",function(o){console.log("Connection open"),o.on("message",function n(n){console.log("-----------------------------------------------"),console.log(n),wss.broadcast(n),console.log("-----------------------------------------------")}),o.on("close",function(o,n){console.log("conneciton closed with code"+o)}),o.on("error",function(o){console.log(o)})}),wss.broadcast=function(o){wss.clients.forEach(function(n){n.send(o)})};